        maxWidth: '800px',
    },
    pageContainer: {
        display: 'flex',
        flexDirection: 'row',
        margin: '20px auto',
    },
    header: {
        textAlign: 'center'
    },
    table: {
        borderRadius: '5px',
        boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0,0.19)',
    },
    tr: {
        textAlign: 'center',
        height: '50px',
    },
    th: {
        borderBottom: '1px solid black',
    },
    btnContainer: {
        textAlign: 'center',
        margin: '10px'
    },
    btn: {
        color: "#fff",
        width: '100px',
        height: '35px',
        margin: '10px',
        fontSize: '20px',
        outline: '0',
        boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0,0.19)',
        background: '#4579d9',
        borderRadius: '5px',
    },
    loader: {
        textAlign: 'center'
    }
}
